typedef struct DynamicFloatArray {
     float *arr;
     size_t length;
} DynamicFloatArray;

DynamicFloatArray *array_init() {
     DynamicFloatArray *arr = malloc(sizeof(DynamicFloatArray));
     arr -> length = 0;
     return arr;
}

void array_free(DynamicFloatArray *arr) {
     if(sizeof(arr -> arr) > 0) free(arr -> arr);
     free(arr);
}

void array_push(DynamicFloatArray *arr, float n) {
