Here is code that performs a canny edge detection using the OpenCV library:

#include <opencv2/opencv.hpp>

cv::Mat applyCannyEdgeDetection(const cv::Mat& inputImage, double threshold1, double threshold2)
{
    // Convert the input image to grayscale
    cv::Mat grayImage;
    cv::cvtColor(inputImage, grayImage, cv::COLOR_BGR2GRAY);

    // Apply Gaussian blur to reduce noise
    cv::Mat blurredImage;
    cv::GaussianBlur(grayImage, blurredImage, cv::Size(5, 5), 0);

    // Apply Canny edge detection
    cv::Mat edges;
    cv::Canny(blurredImage, edges, threshold1, threshold2);

    // Create a mask by thresholding the edges
    cv::Mat mask;
    cv::threshold(edges, mask, 128, 255, cv::THRESH_BINARY);

    // Apply the mask to the input image to show only the edges
    cv::Mat outputImage;
    inputImage.copyTo(outputImage, mask);

    return outputImage;
}

int main()
{
    // Load an input image
    cv::Mat inputImage = cv::imread("input.jpg");

    if (inputImage.empty()) {
        std::cerr << "Failed to load the input image" << std::endl;
        return 1;
    }

    // Apply Canny edge detection to the input image
    double threshold1 = 50.0;
    double threshold2 = 150.0;
    cv::Mat outputImage = applyCannyEdgeDetection(inputImage, threshold1, threshold2);

    // Display the input and output images
    cv::imshow("Input Image", inputImage);
    cv::imshow("Canny Edge Detection", outputImage);
    cv::waitKey(0);

    return 0;
}

