Here's the updated code:

  <script>
    const ball_radius = 50;
    const canvas_width = canvas_height = 600;

    let ball_x = ball_y = ball_radius;
    let x_movement_rate = 3; // number of pixels moved right per frame
    let y_movement_rate = 1; // number of pixels moved down per frame

    function checkForBounce() {
        if (ball_x + ball_radius >= canvas_width || ball_x - ball_radius <= 0) {
            x_movement_rate = x_movement_rate * -1;
        }

        if (ball_y + ball_radius >= canvas_height || ball_y - ball_radius <= 0) {
            y_movement_rate = y_movement_rate * -1;
        }
    }

    function draw() {
        let canvas = document.getElementById("animation_canvas");
        let ctx = canvas.getContext("2d");

        ctx.fillStyle = "blue";
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        ctx.fillStyle = "purple";
        ctx.translate(x_movement_rate, y_movement_rate);

        ctx.beginPath();
        ctx.arc(ball_radius, ball_radius, ball_radius, 0, 2 * Math.PI);
        ctx.fill();

        ball_x += x_movement_rate;
        ball_y += y_movement_rate;

        checkForBounce();

        window.requestAnimationFrame(draw);
    }

    window.requestAnimationFrame(draw);
