            # If the current assignment does not lead to a solution,
            # reset the cell and try the next number
            grid[row][col] = 0

    # If no number can be placed, backtrack
    return False

def find_empty_cell(grid):
    # Find the next empty cell in the grid
    for i in range(9):
        for j in range(9):
            if grid[i][j] == 0:
                return i, j
