def is_valid_move(grid, row, col, num):
    # Check if assigning the number to the given cell violates Sudoku rules

    # Check the row
    for j in range(9):
        if grid[row][j] == num:
            return False

    # Check the column
    for i in range(9):
        if grid[i][col] == num:
            return False

    # Check the 3x3 box
    box_start_row = 3 * (row // 3)
    box_start_col = 3 * (col // 3)
    for i in range(3):
        for j in range(3):
