def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def count_sum_of_primes(n):
    prime_memory = {}

    def count_funct(n, primes):
        if n == 0:
            return 1
        if n < 0:
            return 0
        if (n, tuple(primes)) in prime_memory:
            return prime_memory[(n, tuple(primes))]
        count = 0
        for i in range(len(primes)):
            count += count_funct(n - primes[i], primes[i:])
        prime_memory[(n, tuple(primes))] = count
        return count

    primes = [i for i in range(2, n) if is_prime(i)]
    return count_funct(n, primes)

def input_positive_integer(prompt):
    while True:
        try:
            num = int(input(prompt))
            if num > 0:
                return num
            else:
                print("Please enter a positive integer.")
        except ValueError:
            print("Invalid input. Please enter a valid integer.")

def main():
