        n = input_positive_integer("Enter a positive integer: ")

        if n == 1 or n == 2 or n == 3:
            if is_prime(n):
                print(f"{n} is a prime number, but cannot be represented as the sum of two or more primes.")
            else:
                print(f"{n} isn't a prime number and cannot be represented as the sum of primes.")
        else:
